import logo from "./logo.svg"
import './App.css';
import { TextInput, Container } from 'react-materialize';
import GoogleLogin from '@react-oauth/google';
import { useEffect } from "react";

function App() {
  function handleCallbackResponse(respone) {

  }

  useEffect(() => {
    /* global google */
    google.accounts.id.initialize({
      client_id: "846433388076-d1h9s38cqveue2jkh0849ge3a99k9ho0.apps.googleusercontent.com",
      callback: handleCallbackResponse
    }
    );
    google.accounts.id.render.Button(
      document.getElementById("signInDiv"),
      { theme: "outline", size: "large" }
    )

  }, [])

  return (

    <div className='App'>

      <div className='appAside'>
        <img className="wlogo" src={logo} alt="logo" />
      </div>

      <div className='appForm'>
        <div className='form'>
          <img src={logo} alt="logo" />
          <h5 className='chaomung'>Chào Mừng Trở Lại</h5>
          <TextInput id='username' label="Tài Khoản"></TextInput>
          <TextInput id='password' type='password' label="Mật Khẩu"></TextInput>
          <button className='login-button'>Đăng Nhập</button><br></br>
          <button><i class="fa-brands fa-google"></i>Đăng Nhập Với Google</button>
        </div>
      </div>

    </div>

  );
}

export default App;
